package com.cognixia.jump.coreExamples;

public class DataTypesExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Single line comment
		
		/* Multline comment starts with <-
		 this is not executable code.
		 comments are a place to write good notes for you and others about your program.
		 Multiline comment ends with -> */
		
		// Java Primitive Data Types
			// Data types assigned to variables that are as broken down as possible in Java
			// they have NO subcomponents
		
		// Numerical Data Types
		int nameOfVariable;  // i.e. DECLARING a variable
		
		nameOfVariable = 5;  // initializing and assigning a value to the variable
		
		byte b = 2;
		short s = 1;
		int i = 0; // please use meaningful variable names :)
		final long l = 3;
		
		float f = 5.0f; //f secures the float assignment and not as a double
		double d = 5.0;
		
		// Text
		char c = 'c'; //char is single quotes
		
		// Logical
		boolean bool = true; // or false
		
		System.out.println(l);  //Default command to print to system output which is console.
		
		
		
		System.out.println(l); // we reassigned l from 3 to 20
	}

}
